import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Home.css";
import { Link } from "react-router-dom";
import Modal from "../components/Modal";

const Home = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedGridItem, setSelectedGridItem] = useState(null);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:5000/api/home", {});
        setData(response.data);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleGridItemClicked = async (item) => {
    setSelectedGridItem(item); // Update selected item state

    const response = await axios.get(
      `http://127.0.0.1:5000/api/recommend/${item.song_id}`
    );
    const detailedData = response.data;
    <Modal item={selectedGridItem} detailedData={detailedData} onClose={closeModal} />;
  };

  const closeModal = () => {
    setSelectedGridItem(null); // Clear selected item state to close modal
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }
  return (
    <div className="grid-container">
      {data.map((item, index) => (
        <div
          key={index}
          className="grid-item"
          onClick={() => handleGridItemClicked(item)}
        >
          {/* Display your data here */}
          <p>{item.title}</p>
          <p>{item.artist}</p>
          <p>{item.arousal}</p>
          <p>{item.valence}</p>
        </div>
      ))}
      {selectedGridItem && (
        <Modal item={selectedGridItem} onClose={closeModal} />
      )}
    </div>
  );
};

export default Home;
