import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import Modal from "../components/Modal";
import "./Playlist.css";

const Playlist = () => {
  const [playlists, setPlaylists] = useState([]); // State to store playlists
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedEmotion, setSelectedEmotion] = useState("");
  const [selectedName, setSelectedName] = useState("");
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };
  const handleSelectChange = (e) => {
    setSelectedEmotion(e.target.value);
  };
  const fetchData = async () => {
    try {
      const response = await axios.get(
        "http://127.0.0.1:5000/api/playlists",
        {}
      );
      setData(response.data);
      console.log(response.data);
    } catch (error) {
      setError(error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddPlaylist = async (playlistName) => {
    // Logic to add a new playlist\

    const response = await axios.get("http://127.0.0.1:5000/api/home", {});
    console.log(response.data);
    const songs = response.data.map((song, index) => ({
      arousal: song.arousal,
      valence: song.valence,
      artist: song.artist,
      title: song.title,
      song_id: song.song_id,
    }));

    // Update the data state with the extracted songs
    setData(songs);
    console.log(songs);
    const filteredSongs = songs.filter((song) => {
      if (selectedEmotion === "happy") {
        // Filter songs with high valence and high arousal (happy)
        return song.valence > 0.5 && song.arousal > 0.5;
      } else if (selectedEmotion === "sad") {
        // Filter songs with low valence and low arousal (sad)
        return song.valence <= 0.5 && song.arousal <= 0.5;
      } else if (selectedEmotion === "angry") {
        // Filter songs with high valence and low arousal (angry)
        return song.valence > 0.5 && song.arousal <= 0.5;
      } else if (selectedEmotion === "relaxed") {
        // Filter songs with low valence and high arousal (relaxed)
        return song.valence <= 0.5 && song.arousal > 0.5;
      }
      return false;
    });
    console.log(filteredSongs);
    console.log(selectedName);
    await axios.post("http://127.0.0.1:5000/api/playlists_insert", {
      name: selectedName,
      songs: filteredSongs,
    });
    setPlaylists([...playlists, { name: selectedName, songs: filteredSongs }]);
    closeModal();
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          "http://127.0.0.1:5000/api/playlists",
          {}
        );
        setData(response.data);
        console.log(response.data);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);
  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }
  return (
    <div className="playlists">
      <h1>Playlists</h1>
      <button className="plus-button" onClick={openModal}>
        +
      </button>
      <div className="grid-container">
        {/* Render playlists */}
        {data.map((item, index) => (
          <Link to={`/playlist/${item.name}`} key={index} className="grid-item">
            <h2>{item.name}</h2>
            <p>songs: {item.song_ids[0].split(",").length}</p>
          </Link>
        ))}
      </div>
      {/* Modal for adding new playlist */}
      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Add New Playlist</h2>
            <input
              type="text"
              placeholder="Playlist Name"
              value={selectedName} // Set the value of the input field
              onChange={(e) => setSelectedName(e.target.value)}
            />
            <select
              className="select-field"
              value={selectedEmotion}
              onChange={handleSelectChange}
            >
              <option value="">Select an emotion</option>
              <option value="happy">Happy</option>
              <option value="sad">Sad</option>
              <option value="angry">Angry</option>
              <option value="relaxed">Relaxed</option>
            </select>
            <button onClick={closeModal}>Cancel</button>
            <button onClick={() => handleAddPlaylist("New Playlist")}>
              Add
            </button>
          </div>
        </div>
      )}
    </div>
  );
};
export default Playlist;
