import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Home.css";
import { Link } from "react-router-dom";
import Modal from "../components/Modal";
import styled from "styled-components";
const PlusButton = styled.button`
  position: fixed;
  top: 20px;
  right: 20px;
  background-color: #007bff;
  color: white;
  border: none;
  border-radius: 50%;
  width: 50px;
  height: 50px;
  font-size: 24px;
  cursor: pointer;
  z-index: 999;
`;

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ModalContent = styled.div`
  background-color: white;
  padding: 20px;
  border-radius: 8px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  margin-bottom: 10px;
`;

const Button = styled.button`
  padding: 10px 20px;
  background-color: #007bff;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
`;
const Playlist = () => {
  const [playlists, setPlaylists] = useState([]); // State to store playlists
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleAddPlaylist = (playlistName) => {
    // Logic to add a new playlist
    setPlaylists([...playlists, { name: playlistName, songs: [] }]);
    closeModal();
  };

  return (
    <div className="playlists-page">
      <h1>Playlists</h1>
      <button className="add-playlist-button" onClick={openModal}>
        Add Playlist
      </button>
      <div className="playlist-grid">
        {/* Render playlists */}
        {playlists.map((playlist, index) => (
          <Link to={`/playlist/${index}`} key={index} className="playlist-card">
            <h2>{playlist.name}</h2>
            <p>{playlist.songs.length} songs</p>
          </Link>
        ))}
      </div>
      {/* Modal for adding new playlist */}
      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Add New Playlist</h2>
            <input type="text" placeholder="Playlist Name" />
            <button onClick={closeModal}>Cancel</button>
            <button onClick={() => handleAddPlaylist("New Playlist")}>Add</button>
          </div>
        </div>
      )}
    </div>
  );
};
export default Playlist;
